import React, { Component } from 'react'
import './Showlist.css'
import { getLists, createList, deleteList, updateList } from './actions'
import { connect } from 'react-redux'
import List from './List'

class Showlist extends Component {
    state = {
        name: '',
        price: ''
    }

    componentDidMount() {
        this.props.getLists()
    }

    handleChange = e => {
        var name = e.target.name,
            value = e.target.value
        this.setState({ [name]: value })
    }

    handleSubmit = e => {
        e.preventDefault()
        const { name, price } = this.state
        this.props.createList({
            name, price
        })
        this.setState({
            name: '', price: ''
        })
    }

    handleDelete = id => {
        this.props.deleteList(id)
    }

    handleUpdate = (id, name, price) => {
        this.props.updateList(id, {
            name, price
        })
    }

    render() {
        const { lists } = this.props
        return (
            <div className="con">
                <h1>People Lists</h1>
                <ul>
                    {lists.map((list, index) => {
                        return <List key={index} list={list} onDelete={this.handleDelete} onUpdate={this.handleUpdate} />
                    })}
                </ul>

                <form onSubmit={this.handleSubmit}>
                    <input type="text" name="name" placeholder="Name" onChange={this.handleChange} value={this.state.name} />
                    <input type="number" name="price" placeholder="Price" onChange={this.handleChange} value={this.state.price} />

                    <button type="submit">Add</button>
                </form>
            </div>


        )
    }
}

const mapStateToProps = ({ lists }) => {
    return {
        lists
    }
}
export default connect(
    mapStateToProps,
    { getLists, createList, deleteList, updateList }
)(Showlist)
