import React, { useState, useEffect } from 'react';
import { firestore } from './index'
import Task from './Task'
import './App.css'
import Navbar from './Navbar'
import axios from 'axios'

const App = () => {

  const [covid, setCovid] = useState({})

  useEffect(() => {
    getCovid()
  }, []
  )

  const getCovid = async () => {
    const result = await axios.get('https://covid19.th-stat.com/api/open/today')
    setCovid(result.data)
  }

  console.log(covid)


  const [tasks, setTasks] = useState([

  ])
  const [name, setName] = useState('')
  const [todo, setTodo] = useState('')
  const [passid, setPassid] = useState('')


  useEffect(() => {
    retriveData()
  }, [])

  const retriveData = () => {
    firestore.collection("tasks").orderBy('id', 'asc').onSnapshot(snapshot => {
      console.log(snapshot)
      let myTask = snapshot.docs.map(d => {
        const { id, name, todo, passid } = d.data()
        return { id, name, todo, passid }
      })
      setTasks(myTask)
    })
  }


  const renderTask = () => {
    if (tasks && tasks.length) {
      return tasks.map((task, index) => (
        <Task key={index}
          task={task}
          deleteTask={deleteTask}
          editTask={editTask} />
      )
      )

    } else {
      return (<li>No task</li>)
    }
  }

  const addTask = () => {
    let id = (tasks.length === 0) ? 1 : tasks[tasks.length - 1].id + 1
    firestore.collection("tasks").doc(id + '').set({ id, name, todo, passid })
  }

  const deleteTask = (id) => {
    firestore.collection('tasks').doc(id + '').delete()
  }

  const editTask = (id) => {
    firestore.collection('tasks').doc(id + '').set({ id, name, todo, passid })
  }

  return (

    <div className='app-container'>
      <Navbar />   <br />


      <h1> ข้อมูล CoVid-19 ล่าสุด !!! </h1>
      <p1>
        <div> Confirmed : {covid.Confirmed}                         </div>
        <div> Recovered : {covid.Recovered}                         </div>
        <div> Hospitalized : {covid.Hospitalized}                   </div>
        <div> Deaths : {covid.Deaths}                               </div>
        <div> NewConfirmed : {covid.NewConfirmed}                   </div>
        <div> NewConfirmed : {covid.NewConfirmed}                   </div>
        <div> NewRecovered : {covid.NewRecovered}                   </div>
        <div> NewHospitalized : {covid.NewHospitalized}             </div>
        <div> NewDeaths : {covid.NewDeaths}                         </div>
      </p1>

      <p2>
        <div> UpdateDate : {covid.UpdateDate}                       </div>
      </p2>

      <h1> บันทึก และรายงานทางการแพทย์ </h1>

      <br />  ID pass : <input type="number" name="name" onChange={(e) => setPassid(e.target.value)} />  <br />
      <br />  Name : <input type="text" name="name" onChange={(e) => setName(e.target.value)} />  <br />
      <br />  Todo : <input type="name" name="todo" onChange={(e) => setTodo(e.target.value)} />  <br />
      <br />   <button type="button" class="btn btn-success" onClick={addTask} >Success</button>  <br />
      <br />  <ul style={{ display: 'flex', listStyle: 'none' }} >{renderTask()}</ul>

    </div>
  );
}

export default App;
