import React, { useState, useEffect } from 'react';
import axios from 'axios'
import '../App.css'
import { Collapse, Button, CardBody, Card } from 'reactstrap';
import Bar from './Bar';


// https://reactstrap.github.io/

const Covidrealtime2 = (props) => {

    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => setIsOpen(!isOpen);
    const [covid, setCovid] = useState({})

    useEffect(() => {
        getCovid()
    }, []
    )

    const getCovid = async () => {
        const result = await axios.get('https://covid19.th-stat.com/api/open/cases/sum')
        setCovid(result.data)
    }

    console.log(covid)

    return (

        <div className="app-container">

            <h1> CoVid-19 Realtime.</h1>

            <Button color="danger" onClick={toggle} style={{ marginBottom: '1rem' }}>

                Click to show information
            </Button>
            <Collapse isOpen={isOpen}>
                <Card>
                    <CardBody>
                        <p1>
                            <div> Bangkok :{covid.Bangkok} ]</div>                       
                        </p1>
                        <p2>
                        </p2>
                    </CardBody>
                </Card>
            </Collapse>
        </div>
    );
}

export default Covidrealtime2
