import React, { useState } from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput, MDBIcon } from 'mdbreact';
import '@fortawesome/fontawesome-free/css/all.min.css';
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import './Login.css'
import config from '../config'


const Login = () => {

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const login = e => {

        e.preventDefault();
        config.auth().signInWithEmailAndPassword(email, password).then((u) => {
            console.log(u)
        }).catch((err) => {
            console.log(err)
        })
    }


    const signup = e => {

        e.preventDefault()
        config.auth().createUserWithEmailAndPassword(email, password).then((u) => {
            console.log(u)
        }).catch((err) => {
            console.log(err)
        })
    }


    return (
        <div >
            <MDBContainer>
                <MDBRow className="LoginForm">
                    <MDBCol md="9">
                        <form>

                            <p className="h5 text-center mb-4">Sign up</p>
                            <div className="grey-text">
                                <MDBInput label="Email"  name="email" onChange={(e) => setEmail(e.target.value)} />

                                <MDBInput label="Password" group type="password" name="password" validate onChange={(e) => setPassword(e.target.value)} />
                            </div>
                            <div className="text-center">
                                <MDBBtn  size="md" color="success" onClick={login}><MDBIcon icon="sign-in-alt" className="mr-2" />Login</MDBBtn>
                                <MDBBtn  size="md" color="primary" onClick={signup}> <MDBIcon icon="user" className="mr-2" />Register</MDBBtn>

                            </div>
                        </form>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        </div>
    )
}

export default Login;
