import React, { Component } from 'react'

export default class List extends Component {
	state = {
		isEdit: false,
		name: this.props.list.name,
		price: this.props.list.price
	}

	handleChange = e => {
		var name = e.target.name,
			value = e.target.value
		this.setState({ [name]: value })
	}

	render() {
		const { list, onDelete, onUpdate } = this.props
		const { isEdit, name ,price} = this.state

		if (isEdit === true) {
			return (
				<li key={list.id}>
					{list.id}. <input type="text" name="name" value={name} onChange={this.handleChange} />
					<input type="number" name="price" value={price} onChange={this.handleChange} />

					<button
						onClick={() => {
							onUpdate(list.id, name , price)
							this.setState({ isEdit: false })
						}}
					>
						Save
					</button>
					<button onClick={() => this.setState({ isEdit: false, name: list.name , price: list.price })}>Cancel</button>
				</li>
			)
