import React from 'react';
import './InputForm.css';
import { useDispatch, useSelector } from 'react-redux'
import axios from 'axios'
import '@fortawesome/fontawesome-free/css/all.min.css';
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBIcon } from 'mdbreact';


const InputForm = props => {
    const { data, onChange } = props;

    const dispatch = useDispatch({})
    const covids = useSelector(state => state.covids)
    const form = useSelector(state => state.form)

    const addCovid = async () => {
        const result = await axios.post(`http://localhost:8080/api/covids/`, form)
        dispatch({
            type: "ADD_COVID",
            covid: { ...form, id: covids.length > 0 ? covids[covids.length - 1].id + 1 : 0 }
        })
    }

    return (
        <div className='form'>
            <h2>บันทึกทางการเเพทย์</h2>
                <div>
                    <tr>
                        <td>รหัสผู้ป่วย</td>
                        <td>
                            <input className='input' type="number" onChange={(e) => dispatch({ type: "CHANGE_NO", no: e.target.value })} />
                        </td>
                    </tr>

                    <tr>
                        <td>วันที่</td>
                        <td>
                            <input className='input' type="text" onChange={(e) => dispatch({ type: "CHANGE_DATE", date: e.target.value })} />
                        </td>
                    </tr>


                    <tr>
                        <td>ชื่อผู้ป่วย</td>
                        <td>
                            <input className='input' type="text" onChange={(e) => dispatch({ type: "CHANGE_NAME", name: e.target.value })} />
                        </td>
                    </tr>


                    <tr>
                        <td>อาการผู้ป่วย</td>
                        <td>
                            <input className='input' type="text" onChange={(e) => dispatch({ type: "CHANGE_TODO", todo: e.target.value })} />
                        </td>
                    </tr>

                    <tr>
                        <td>รูปผู้ป่วย</td>
                        <td>
                            <input className='input' type="text" onChange={(e) => dispatch({ type: "CHANGE_IMG", img: e.target.value })} /> <br />
                        </td>
                    </tr>
                    <tr>
                        <td></td>
                        <td>
                        <MDBBtn size="md" color="success" onClick={addCovid}><MDBIcon icon="sign-in-alt" className="mr-2" />SUBMIT</MDBBtn>
                        </td>
                    </tr>
                </div>
        </div>
    )
}

export default InputForm
